import React from "react";
import axios from 'axios';
import './Usertweetsdisplay.css';

const UsertweetsUpdate=()=>{
    const[title,setTitle]=useState('');
    const[description,setDescription]=useState('');

    const updateTweetId= async()=>{
        const requestbody={
            title:title,
            tweet:description
        }
        try{
        const res=axios.put(`http://localhost:8081/tweets/${username}/update/${id}`,requestbody)
        console.log('Item updated successfully')
        console.log(res);
        swal("Success","Tweet updated Successfully",{
            buttons:false,
            timer:2000})
        }catch(error){alert(error)}
        
    }

    return(
    <form onSubmit={updateTweetId}>
        <div className="box1">
            <textarea 
             rows="2" 
             cols="50" 
             name="title"  
             placeholder="Add the title" 
             value={title}
             onChange={(e)=>{setTitle(e.target.value)}}
             required />
        </div>
    <div className="box1">
        <textarea 
        rows="5" 
        cols="50"
        name="description" 
        placeholder="Want to express ? Express it." 
        value={description}
        onChange={(e)=>{setDescription(e.target.value)}}
        required />
    </div>

<div className="button">
    <Button type="submit" color="primary" variant="contained">Update Tweet</Button>
        </div>
       </form>
    )
}

export default UsertweetsUpdate;
