import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import image1 from '../../assets/registration-updated.jpg';
import {MDBBtn,MDBContainer,MDBCard,MDBCardBody,MDBInput} from 'mdb-react-ui-kit';
import './Registration.css';
const Registration=()=>{

    return(
        <>
          <MDBContainer fluid className='d-flex align-items-center justify-content-center bg-image' style={{backgroundImage:`url(${image1})`}}>
            <div className='mask gradient-custom-3'></div>
            <MDBCard className='m-5' style={{maxWidth:'1000px'}}>
                <MDBCardBody className='px-5'>
                    <h2 className="text-uppercase text-center mb-5">Register now!</h2>
                    <MDBInput wrapperClass='mb-4' label='First Name' size='lg' id='form1' type='text' />
                </MDBCardBody>
            </MDBCard>
          </MDBContainer>

        </>
    )
}

export default Registration;
