import React, { useEffect, useState } from "react";
import Card from "../UI/Card/Card";
import axios from 'axios';
const Displaytweets=()=>{
    const [tweetList,setTweetList]=useState([]);
    const displayTweetHandler=async(e)=>{
        //e.preventDefault();

        try{
            const response=await axios.get(`http://localhost:8081/all`);
            console.log(response.data);
            let listtweet=response.data;
            
            let tweetListObj=[];
            listtweet.map((tweet)=>{
                tweetListObj.push(JSON.parse(tweet));
            })
            setTweetList(tweetListObj);
            console.log(tweetListObj);
            

        }catch(e){console.log(e)}
    }

    useEffect(()=>{
       
            displayTweetHandler();
        
    },[]);

    return(
        <>
            <Card>
                <div>
                {tweetList.map(tweet=>(
                    <li key="{index}">{tweet.loginId}</li>
                ))}
                </div>
            </Card>
        
        </>
    )

}

export default Displaytweets;
