import React, { useEffect, useState } from "react";
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import ChatBubbleIcon from '@mui/icons-material/ChatBubble';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import DeleteIcon from '@mui/icons-material/Delete';
import Button from '@material-ui/core/Button';
import CreateIcon from '@mui/icons-material/Create';
import './Usertweetsdisplay.css';
import swal from'sweetalert';
const Usertweetsdisplay = () =>{
    const loginId=localStorage.getItem('usrname');
    const [usertweets,setUsertweets]=useState([]);
    
    const getUsertweets= async ()=>{
       const response=await axios.get(`http://localhost:8081/tweets/${loginId}`)
      console.log(response.data);
      const listtweet=await response.data
      setUsertweets(listtweet);
    }
    const deleteTweetById= async(username,id)=>{
        try{
        const res=axios.delete(`http://localhost:8081/tweets/${username}/delete/${id}`)
        console.log('Item deleted successfully')
        console.log(res);
        swal("Success","Deleted Successfully",{
            buttons:false,
            timer:2000})
        }catch(error){alert(error)}
        
    }
    useEffect(() => {
        getUsertweets();
       
    },[]);
    return(
        <> 
        <br></br>          
        {
            usertweets.map((i) => {
               
                return(
                <>
                    
                    <div className="box">
                    <div className="tweetdisplay">
                    <div className="tweetdisplay__body">
                        <div className="tweetdisplay__header">
                            <div className="tweetdisplay_headerText">
                                <h5>
                                    {i.loginId}
                                    <span className="tweetdisplay__createDate">formattedDate({i.creationDate})</span>
                                </h5>
                            </div>
                            <div className="tweetdisplay__headerTitle">
                                    <h4>{i.title}</h4>
                            </div>
                            <div className="tweetdisplay__headerDescription">
                                <p>{i.tweet} </p>
                            </div>
                        </div>
                        <div className="tweetdisplay__footer" >
                           
                            <Button color="primary" variant="contained"><ChatBubbleIcon fontSize="small" >
                                
                            </ChatBubbleIcon>
                            </Button>
                            <Button color="primary" variant="contained"><ThumbUpIcon fontSize="small" />{i.likes}</Button>
                            <Button color="primary" variant="contained" onClick={()=>deleteTweetById(i.loginId,i.tweetId)}><DeleteIcon fontSize="small" /></Button>
                            <Button color="primary" variant="contained"><CreateIcon fontSize="small" /></Button>

                        </div>
                    </div>
                </div>
                </div>
               
                </>
                )
            })
        }

  


        </>
    )
}

export default Usertweetsdisplay;
