import React, { useEffect, useState } from "react";
import axios from 'axios';
import './Users.css';
import { useNavigate } from "react-router";

const Users=(props)=>{
    const[users,setUsers]=useState([]);
    const navigate=useNavigate();
    const handleAllUsers=async(e)=>{
        e.preventDefault();
        
        try{
         const response=await axios.get('http://localhost:8081/users/all');
         
        
         let userdata=response.data;
         let userObj=[];
         userdata.map((user)=>{
             userObj.push(JSON.parse(user));
         })
         setUsers(userObj)
         console.log(userObj);
      
        }
        catch(e){console.log(e);}
    
    };

    useEffect(() => {
       
        handleAllUsers();
    
    },[]);

    return(
        <>
         <div className="header" >
           <a href="" className="logo">Just Tweet it !</a>

           <div className="header-left">
              <a href="" onClick={()=>navigate("/homepage")}> Tweets </a>
              <a href=""  onClick={()=>navigate("/homepage/yourtweets")}> Your Tweets </a>
              <a href="" className="active"> Users </a>
           </div>
            <div className="header-right">
                <a href="" >Logout</a>
            </div>      
       </div>

            <div><AllUsersDisplay /></div>
        </>
    )
}

export default Users;
