import React, { useEffect, useState } from "react";
import './DisplayTweets.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import Moment from 'moment';
import ChatBubbleIcon from '@mui/icons-material/ChatBubble';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import Button from '@material-ui/core/Button';
import axios from 'axios';


const Displaytweets=()=>{
    const [tweetList,setTweetList]=useState([]);
    const [isLikePressed,setIslikePressed]=useState(false);

    const formattedDate =(date)=> {
        let formatteddate=Moment(date).format("DD/MM/YYYY");
    }
    

      
    const displayTweetHandler=async(e)=>{
        //e.preventDefault();

        try{
            const response=await axios.get(`http://localhost:8081/all`);
            console.log(response.data)
            const listtweet=await response.data;
            setTweetList(listtweet);
            console.log(listtweet);

            }catch(e){console.log(e)}
    }

    const likeTweetHandler=async(likespressed,tweetId)=>{
        const loginId=localStorage.getItem('usrname');
        const rqbody={
            islikePressed:likespressed
        }
        
        try{
            setIslikePressed(likespressed);
            const res=await axios.put(`http://localhost:8081/tweets/${loginId}/like/${tweetId}`,rqbody)
            console.log(res);
        }catch(e){console.log(e);}
        setIslikePressed(false)
    }

    useEffect(() => {
       
            displayTweetHandler();
            
    },[]);

    return(
        <>
           
                {
                    tweetList.map((i) => {
                       
                        return(
                        <>
                            
                            <div className="box">
                            <div className="tweetdisplay">
                            <div className="tweetdisplay__body">
                                <div className="tweetdisplay__header">
                                    <div className="tweetdisplay_headerText">
                                        <h5>
                                            {i.loginId}
                                            <span className="tweetdisplay__createDate">{Moment(i.date).format("DD/MM/YYYY","HH:mm")}</span>
                                        </h5>
                                    </div>
                                    <div className="tweetdisplay__headerTitle">
                                            <h4>{i.title}</h4>
                                    </div>
                                    <div className="tweetdisplay__headerDescription">
                                        <h6>{i.tweet} </h6>
                                    </div>
                                </div>
                                <div className="tweetdisplay__footer" >
                                   
                                    <Button color="primary" variant="contained"><ChatBubbleIcon fontSize="small" ></ChatBubbleIcon>
                                    </Button>
                                    <Button color="primary" variant="contained" onClick={(e)=>likeTweetHandler(true,i.tweetId)}><ThumbUpIcon fontSize="small" />{i.likes}</Button>
                                   
                                </div>
                            </div>
                        </div>
                        </div>
                        
                        </>
                        )
                    })
                }

          
        
        </>
    )

}

export default Displaytweets;
