import React, { useEffect, useState } from "react";
import './DisplayTweets.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import Moment from 'moment';
import ChatBubbleIcon from '@mui/icons-material/ChatBubble';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import DeleteIcon from '@mui/icons-material/Delete';
import Button from '@material-ui/core/Button';
import axios from 'axios';
import { Stack } from "@mui/system";

const Displaytweets=()=>{
    const [tweetList,setTweetList]=useState([]);

    const formattedDate =(date)=> {
        let formatteddate=Moment(date).format("DD/MM/YYYY");
    }
      
      
    const displayTweetHandler=async(e)=>{
        //e.preventDefault();

        try{
            const response=await axios.get(`http://localhost:8081/all`);
            console.log(response.data)
            const listtweet=await response.data;
            setTweetList(listtweet);
            console.log(listtweet);

            }catch(e){console.log(e)}
    }

    useEffect(() => {
       
            displayTweetHandler();
        
    },[]);

    return(
        <>
           
                {
                    tweetList.map((i) => {
                       
                        return(
                        <>
                             <br></br>
                            <div className="box">
                            <div className="tweetdisplay">
                            <div className="tweetdisplay__body">
                                <div className="tweetdisplay__header">
                                    <div className="tweetdisplay_headerText">
                                        <h5>
                                            {i.loginId}
                                            <span className="tweetdisplay__createDate">formattedDate({i.creationDate})</span>
                                        </h5>
                                    </div>
                                    <div className="tweetdisplay__headerTitle">
                                            <h4>{i.title}</h4>
                                    </div>
                                    <div className="tweetdisplay__headerDescription">
                                        <p>{i.tweet} </p>
                                    </div>
                                </div>
                                <div className="tweetdisplay__footer" >
                                   
                                    <Button color="primary" variant="contained" onClick={likeButtonHandler}><ChatBubbleIcon fontSize="small" >
                                        {i.likes}
                                    </ChatBubbleIcon>
                                    </Button>
                                    <Button color="primary" variant="contained"><ThumbUpIcon fontSize="small" /></Button>
                                    <Button color="primary" variant="contained"><DeleteIcon fontSize="small" /></Button>
                                </div>
                            </div>
                        </div>
                        </div>
                        <br></br>
                        </>
                        )
                    })
                }

          
        
        </>
    )

}

export default Displaytweets;
