import React, { useEffect, useState } from "react";
import Card from "../UI/Card/Card";
import axios from 'axios';
const Displaytweets=()=>{
    const [tweetList,setTweetList]=useState([]);
    const displayTweetHandler=async(e)=>{
        //e.preventDefault();

        try{
            const response=await axios.get(`http://localhost:8081/all`);
            console.log(response);
            setTweetList(response.data);

        }catch(e){console.log(e)}
    }

    useEffect(()=>{
        displayTweetHandler();
        
    },[]);

    return(
        <>
            <Card>
                {tweetList.map((tweets)=>{
                    <li>
                        <div>{tweets.loginId}</div>
                        <div>format{tweets.creationDate}</div>
                        <div>{tweets.title}</div>
                        <div>{tweets.tweet}</div>
                    </li>
                })}
                   
                
            </Card>
        
        </>
    )

}

export default Displaytweets;
