import React, { useEffect, useState } from "react";
import  Card  from "react-bootstrap/Card";
import './UsersDisplay.css';
import axios from 'axios';
import Container from 'react-bootstrap/Container';
const UsersDisplay=()=>{
    const[userList,setUserlist]=useState([])
    const UserDisplayHandler=async()=>{
        const response=await axios.get(`http://localhost:8081/users/all`)
        const res=await response.data;
        console.log(res);
        setUserlist(res);
    }
    useEffect(()=>{
        UserDisplayHandler();
    },[])
    return(
        <>
        <div className="container-fluid mt-5">
        <div className="row text-center">
        {
            userList.map((user) => {
                return(
                <>
                    <div className="col-10 col-md-4 mt-5">
                            <Card style={{width:'18rem'}}>
                                <Card.Body>
                                    <Card.Title><h4 style="color:Navy;">{user.loginId}</h4></Card.Title>
                                    <br></br>
                                    <Card.Text >
                                        <h6>Firstname : {user.firstName}</h6>
                                    </Card.Text>
                                    <Card.Text >
                                        <h6>Lastname : {user.lastName}</h6>
                                    </Card.Text>
                                    <Card.Link href="">Get tweet</Card.Link>
                                </Card.Body>
                            </Card>
                        </div>
                    

                
                </>
                )
            })
        
        }
        </div>
        </div>
        </>
    )
}

export default UsersDisplay;
