import React, { useState } from "react";
import axios from 'axios';
import './Users.css';

const Users=(props)=>{
    const[users,setUsers]=useState([]);
    const handleAllUsers=async(e)=>{
        e.preventDefault();
        
        try{
         const response=await axios.get('http://localhost:8081/users/all');
         
        
         let userdata=response.data;
         let userObj=[];
         userdata.map((user)=>{
             userObj.push(JSON.parse(user));
         })
         setUsers(userObj)
         console.log(userObj);
      
        }
        catch(e){console.log(e);}
    
    };

    return(
        <>
         <div className="header" >
           <a href="" className="logo">Just Tweet it !</a>

           <div className="header-left">
              <a href="" > Tweets </a>
              <a href=""> Your Tweets </a>
              <a href="" className="active"> Users </a>
           </div>
            <div className="header-right">
                <a href="" >Logout</a>
            </div>      
       </div>

       <div onClick={handleAllUsers}>
           <div><button type='button'>All users</button></div>
           <ul>

            {users.map(user=>(
                    <li key="{index}">{user._id}</li>
                ))}
           </ul>
       </div>
        </>
    )
}

export default Users;
