import React, { useEffect, useState } from "react";
import Layout from "../UI/Layout/Layout";
import { ListItem,ListItemText } from "@material-ui/core";
import Moment from 'moment';
import axios from 'axios';
import './Comments.css';
import {useHistory,useParams,useNavigate} from "react-router-dom";
const Comments=()=>{
    const {id}=useParams()
    const[commentList,setCommentList]=useState([]);
    const[tweet,setTweet]=useState([]);

    const getUserComments= async ()=>{
        const response=await axios.get(`http://localhost:8081/tweets/all/comments/${id}`)
       console.log(response.data);
       const listComment=await response.data
       setCommentList(listComment);
     }


     const getUsertweet= async ()=>{
        const response=await axios.get(`http://localhost:8081/tweetsbyId/${id}`)
       console.log(response.data);
       const tweets=await response.data
       setTweet(tweets);
     }

     useEffect(()=>{
        getUsertweet();
        getUserComments();
    },[])
    return(
        <>
            <div>
                <Layout />
            </div>
            {
                <>
                return(
                    <>
                        
                        <div className="post">
                            <div className="post__header">
                                <ListItem>
                                    <ListItemText  primary={<h5 style={{fontWeight:"bold"}}>{tweet.loginId}</h5>}
                                    secondary={<h6 style={{fontWeight:"bold"}}>{Moment(tweet.creationDate).format("DD/MM/YYYY","HH:mm")}</h6>} /> 
                                </ListItem>  
                                <ListItem>
                                        <h5>{tweet.title}</h5>
                                </ListItem>
                                <ListItem>
                                    <h6>{tweet.tweet} </h6>
                                </ListItem>
                        </div>
                    </div>
                    
                   
                    </>
                    )
                    </>
                }
            <div>

            </div>
            <div ><h5 style={{textAlign:"center"}}>Comments</h5></div>
            {
            commentList.map((i) => {
               
                return(
                <>
                    
                    <div className="post1">
                        <div className="post__header">
                            <ListItem>
                                <ListItemText  primary={<h5 style={{fontWeight:"bold"}}>{i.loginId}</h5>}
                                secondary={<h6 style={{fontWeight:"bold"}}>{Moment(i.commentPostedDate).format("DD/MM/YYYY","HH:mm")}</h6>} /> 
                            </ListItem>  
                            <ListItem>
                                <h6>{i.comment} </h6>
                            </ListItem>
                    </div>
                </div>
                
               
                </>
                )
            })
        }
        </>

    )
}

export default Comments;
