import React,{useState} from 'react';
import Card from '../UI/Card/Card';
import './login.css';
import {useNavigate,useParams} from 'react-router-dom';
import swal from'sweetalert';
import axios from 'axios';


const Login=(props)=>{
    const [username,setUserName]=useState('');
    const [password,setPassword]=useState('');
    
    const navigate=useNavigate();
    
    const handleSubmit=async(e)=>{
        e.preventDefault();
        const requestBody={
            loginId:username,
            password: password
        }
        
        console.log(requestBody);
        try{
         const response=await axios.post('http://localhost:8081/login',requestBody)
         let token=response.data.token;
         let usrname=response.data.loginId;
         localStorage.setItem('usrname',usrname);
         localStorage.setItem('token',token);
         if(token !== null){
            swal("Success","Successfully logged in",{
                buttons:false,
                timer:2000,
            })}
            navigate('/homepage')
         console.log(response);
        }
        catch(e){
            swal("Inavlid username or password");
            console.log(e);}
       
        setUserName('');
        setPassword('');
    };
    const usernameHandler=(e)=>{
        setUserName(e.target.value)
       
    }
    const passwordHandler=(e)=>{
        setPassword(e.target.value)
    }
    return(
    <>
    <div className="image1">
    <Card>
        <form  onSubmit={handleSubmit}> 
       
            <h1>Lets Tweet today!</h1> 
            <div className='login_top'>
            <div> <h2>Sign in</h2> </div>
            <div className='login_control'>
                <label>Username</label>
                <input
                    type='text'
                    id= 'username'
                    name ='username'
                    value={username}
                    onChange={usernameHandler}/>
            </div>
            <div className='login_control'>
                <label>Password</label>
                <input
                    type='text' 
                    id='password'
                    name='password'
                    value={password}
                    onChange={passwordHandler}/>
            </div>
            </div>
            <div className='login_actions_submit1'>
                <button type='submit' >Submit</button>
               
            </div>
            
            <div className='login_actions_register'>
               <a href="">Register</a>
            </div>
            <div className='login_actions_forgotPassword'>
               <a href="">Forgot  password</a>
            </div>
           
            </form>
        </Card>
        </div>
      
        
           
        </>
    )
}

export default Login;
