import React,{useEffect, useState} from "react";
import axios from 'axios';
import Button from '@material-ui/core/Button';
import swal from'sweetalert';
import {useHistory,useParams} from "react-router-dom";
import './UsertweetsUpdate.css';

const UsertweetsUpdate=()=>{
    const [user,setUser]=useState({
        title:"",
        tweet:""
    });
    const{ username }=useParams();
    const { id }=useParams();

    useEffect(()=>{
        const getUser=async()=>{
            try{
                const res=await axios.get(`http://localhost:8081/tweets/${username}/${id}`)
                const res1=await res.data;
                setUser(res1)

            }catch(error){console.log(error)}
        }
        getUser();
    },[username,id]);
    const onTextChange=(e)=>{
        setUser({
        ...user,
        [e.target.value]: e.target.value
        })
    }
    const updateTweetId= async(e)=>{
        e.preventDefault()
        try{
        
        const res=axios.put(`http://localhost:8081/tweets/${username}/update/${id}`,user)
        console.log('Item updated successfully')
        console.log(res);
        swal("Success","Tweet updated Successfully",{
            buttons:false,
            timer:2000})
        }catch(error){alert(error)}
        
    }

    return(
    <form onSubmit={updateTweetId}>
        <div className="box1">
            <textarea 
             rows="2" 
             cols="50" 
             name="title"  
             placeholder="Add the title" 
             value={user.title}
             onChange={(e)=>{onTextChange(e)}}
             required />
        </div>
    <div className="box1">
        <textarea 
        rows="5" 
        cols="50"
        name="description" 
        placeholder="Want to express ? Express it." 
        value={user.tweet}
        onChange={(e)=>{onTextChange(e)}}
        required />
    </div>

<div className="button">
    <Button type="submit" color="primary" variant="contained">Update Tweet</Button>
        </div>
       </form>
    )
}

export default UsertweetsUpdate;
