import React, { useEffect, useState } from "react";
import axios from 'axios';
const AllUsersDisplay=()=>{
    const[users,setUsers]=useState([]);
   
    const handleAllUsers=async(e)=>{
        e.preventDefault();
        
        try{
         const response=await axios.get('http://localhost:8081/users/all');
         
        
         let userdata=response.data;
         let userObj=[];
         userdata.map((user)=>{
             userObj.push(JSON.parse(user));
         })
         setUsers(userObj)
         console.log(userObj);
      
        }
        catch(e){console.log(e);}
    
    };

    useEffect(() => {
       
        handleAllUsers();
    
    },[]);

    return(
        <div>
           <ul>
            {users.map(user=>(
             <li key="{index}">{user._id}</li>
         ))}
        </ul>
        </div>
    )
}

export default AllUsersDisplay;
