import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import Card from 'react-bootstrap/Card';
import image1 from '../../assets/registrationpage.jpg';
import './Registration.css';
const Registration=()=>{

    return(
        <section class="vh-100">
        <div class="container-fluid">
            <div class="row">
                <div class="col-sm-6 px-0 d-none d-sm-block">
                    <img src={image1} class="w-100 vh-100"  alt="sample image"></img>
                </div>
    
                <div class="col-sm-6 text-black">
                    <div class="p-4 p-lg-5 text-black">
                        <form >
                        <div class="d-flex align-items-center mb-3 pb-1">
                        <h2 class="h2 fw-bold mb-0">Register now! </h2>
                        </div>
                            <h3 className="h3-text" class="fw-normal mb-3 pb-3">Log in</h3>
                            <div class="form-outline mb-4">
                            <label class="form-label" htmlFor="username">First name</label>
                                <input type="text"
                                        id="firstname"
                                        name="firstname"
                                        //value={username}
                                        //onChange={usernameHandler}
                                        class="form-control form-control-lg" />
                               
                               
                            </div>
                            <div class="form-outline mb-4">
                            <label class="form-label" htmlFor="lastname">Lastname</label>
                                <input type="text" 
                                       id="lastname"
                                       name="lastname"
                                       //value={password}
                                       //onChange={passwordHandler}
                                       class="form-control form-control-lg" />
                               
    
                            </div>
                            <div class="form-outline mb-4">
                            <label class="form-label" htmlFor="email">Email</label>
                                <input type="email" 
                                       id="email"
                                       name="email"
                                       //value={password}
                                       //onChange={passwordHandler}
                                       class="form-control form-control-lg" />
                               
    
                            </div>
                            <div class="pt-1 mb-4">
                                <button class="btn btn-info btn-lg btn-block" type="submit">Register</button>
                            </div>
                            
                        </form>
                    </div>
                </div>
          </div>
        </div> 
        </section>
        )
}

export default Registration;
