import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import Card from 'react-bootstrap/Card';
import image1 from '../../assets/registrationpage.jpg';
import Button from '@material-ui/core/Button';

const ForgotPassword=()=>{

    return(
        <section class="vh-100">
        <div class="container-fluid">
            <div class="row">
                <div class="col-sm-6 px-0 d-none d-sm-block">
                    <img src={image1} class="w-100 vh-100"  alt="sample image"></img>
                </div>
    
                <div class="col-sm-6 text-black">
                <div class="p-4 p-lg-5 text-black">
                    <form>
                    <div class="d-flex align-items-center mb-3 pb-1">
                    <h2 class="h2 fw-bold mb-0">forgot password? Don't worry change now. </h2>
                   
                    <div class="form-outline mb-4">
                    <label class="form-label" htmlFor="password">Password</label>
                            <input type="password"
                                    id="password"
                                    name="password"
                                    //value={username}
                                    //onChange={usernameHandler}
                                    placeholder="Password"
                                    class="form-control form-control-lg" />
                           
                        </div>
                        <div class="form-outline mb-4">
                        <label class="form-label" htmlFor="password">Confirm password</label>
                            <input type="password"
                                    id="confirmPassword"
                                    name="confirmPassword"
                                    //value={username}
                                    //onChange={usernameHandler}
                                    placeholder="Repeat password"
                                    class="form-control form-control-lg" />
                           
                        </div>
                        
                    </div>
                    <Button color="primary" variant="contained">update Password</Button>
                    <div>
                    <p>back to login Page <a href="" class="link-info">Login here</a></p>
                    </div>
                    
                    </form>
                </div>
                </div>
          </div>
        </div> 
        </section>
        )
}

export default ForgotPassword;
