import React,{useState} from 'react';
import Card from '../UI/Card/Card';
import './login.css';
import {useNavigate} from 'react-router-dom';
import Homepage from '../Homepage/Homepage';
//import swal from'sweetalert';

const Login=(props)=>{
    const [username,setUserName]=useState();
    const [password,setPassword]=useState();
    const navigate=useNavigate();
    const handleSubmit=(e)=>{
        e.preventDefault();
        
        const response={
            username:username,
            password:password
        };
       /* if('accessToken' in response){
            swal("Success","success",{
                buttons:false,
                timer:2000,
            })
            .then((value)=>{
                localStorage.setItem('accessToken',response['accessToken']);
                localStorage.setItem('user',JSON.stringify(response['user']));
                window.location.href="/homePage";
            });
        
        }else{
            swal("Failed","error");
        }*/
        window.location.href="/homepage";
       
        setUserName('');
        setPassword('');
    };
    const usernameHandler=(e)=>{
        setUserName(e.target.value)
    }
    const passwordHandler=(e)=>{
        setPassword(e.target.value)
    }
    return(
    <>
    <div className="image1">
    <Card>
        <form onSubmit={handleSubmit}> 
            <h1>Lets Tweet today!</h1> 
            <div className='login_top'>
            <div> <h2>Sign in</h2> </div>
            <div className='login_control'>
                <label>Username</label>
                <input
                    type='text'
                    id= 'username'
                    name ='username'
                    onChange={usernameHandler}/>
            </div>
            <div className='login_control'>
                <label>Password</label>
                <input
                    type='text' 
                    id='password'
                    name='password'
                    onChange={passwordHandler}/>
            </div>
            </div>
            <div className='login_actions_submit1'>
                <button type='submit' onSubmit={()=>navigate('homepage')}>Submit</button>
            </div>
            
            <div className='login_actions_register'>
               <a href="">Register</a>
            </div>
            <div className='login_actions_forgotPassword'>
               <a href="">Forgot  password</a>
            </div>
           
            </form>
        </Card>
        </div>
       
          <Homepage username={response.username} />
           
        </>
    )
}

export default Login;
