import React, { useEffect, useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
const Displaytweets=()=>{
    const [tweetList,setTweetList]=useState([]);
    const displayTweetHandler=async(e)=>{
        //e.preventDefault();

        try{
            const response=await axios.get(`http://localhost:8081/all`);
            console.log(response.data);
            let listtweet=response.data.json;
            
            console.log(listtweet);
            

        }catch(e){console.log(e)}
    }

    useEffect(()=>{
       
            displayTweetHandler();
        
    },[]);

    return(
        <>
            
                <div className="container-fluid mt-5">
                    <div className="row text-center">
                        <div className="col-10 col-md-4 mt-5">
                            <div class="card p-2">
                                <div class="d-flex align-items-center">
                                    <div class="ml-3 w-100">
                                    <h4 class="mb-0 mt-0 textLeft">Swagatika</h4><span className="textLeft"></span>
                                    
                                </div>
                                </div>
                            </div>

                        </div>

                    </div>
                </div>
            
        
        </>
    )

}

export default Displaytweets;
