import React,{useState} from 'react';
import Card from '../UI/Card/Card';
import './login.css';
import {useNavigate} from 'react-router-dom';
import Homepage from '../Homepage/Homepage';
import swal from'sweetalert';

const loginUser=async (username,password)=>{
    const data = await fetch('http://localhost:8081/login', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            username : username,
            password : password})
    });
    return await data.json();

}
const Login=(props)=>{
    const [username,setUserName]=useState();
    const [password,setPassword]=useState();
    const navigate=useNavigate();
    const handleSubmit=async(e)=>{
        e.preventDefault();
        
        const response= await loginUser({
            username,
            password
        });
       
        if('token' in response){
            swal("Success",response.message,"success",{
                buttons:false,
                timer:2000,
            })
            .then((value)=>{
                localStorage.setItem('token',response['token']);
                window.location.href="/homepage";
            });
        
        }else{
            swal("Failed",response.message,"error");
        }
        
       
        setUserName('');
        setPassword('');
    };
    const usernameHandler=(e)=>{
        setUserName(e.target.value)
       
    }
    const passwordHandler=(e)=>{
        setPassword(e.target.value)
    }
    return(
    <>
    <div className="image1">
    <Card>
        <form noValidate onSubmit={handleSubmit}> 
            <h1>Lets Tweet today!</h1> 
            <div className='login_top'>
            <div> <h2>Sign in</h2> </div>
            <div className='login_control'>
                <label>Username</label>
                <input
                    type='text'
                    id= 'username'
                    name ='username'
                    onChange={usernameHandler}/>
            </div>
            <div className='login_control'>
                <label>Password</label>
                <input
                    type='text' 
                    id='password'
                    name='password'
                    onChange={passwordHandler}/>
            </div>
            </div>
            <div className='login_actions_submit1'>
                <button type='submit' >Submit</button>
            </div>
            
            <div className='login_actions_register'>
               <a href="">Register</a>
            </div>
            <div className='login_actions_forgotPassword'>
               <a href="">Forgot  password</a>
            </div>
           
            </form>
        </Card>
        </div>
       
         <div><Homepage username={username} /></div>
           
        </>
    )
}

export default Login;
