import React, { useEffect, useState } from "react";
import './DisplayTweets.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import ChatBubbleOutlineIcon from "@material-ui/icons/ChatBubbleOutline"

import axios from 'axios';
const Displaytweets=()=>{
    const [tweetList,setTweetList]=useState([]);
    const displayTweetHandler=async(e)=>{
        //e.preventDefault();

        try{
            const response=await axios.get(`http://localhost:8081/all`);
            console.log(response.data);
            let listtweet=response.data.json;
            
            console.log(listtweet);
            

        }catch(e){console.log(e)}
    }

    useEffect(()=>{
       
            displayTweetHandler();
        
    },[]);

    return(
        <>
            <div className="tweetdisplay">
                <div className="tweetdisplay__body">
                    <div className="tweetdisplay__header">
                        <div className="tweetdisplay_headerText">
                            <h3>
                                Hii!
                            </h3>
                        </div>
                        <div className="tweetdisplay__headerTitle">
                                <h4>first post</h4>
                        </div>
                        <div className="tweetdisplay__headerDescription">
                            <p>This is my first post</p>
                        </div>
                    </div>
                    <div className="tweetdisplay__footer">
                        <ChatBubbleOutlineIcon fontsize="small" />
                    </div>
                </div>
            </div>
            
        
        </>
    )

}

export default Displaytweets;
