import React,{useState} from 'react';
import Card from '../UI/Card/Card';
import './login.css';
import {useNavigate} from 'react-router-dom';
import Homepage from '../Homepage/Homepage';
import swal from'sweetalert';
import axios from 'axios';
   
const Login=(props)=>{
    const [username,setUserName]=useState();
    const [password,setPassword]=useState();
    const navigate=useNavigate();
    const handleSubmit=async(e)=>{
        e.preventDefault();
        const requestBody={
            username:username,
            password: password
        }
        console.log(requestBody);
        try{
       const response=await axios.post('http://localhost:8081/login',requestBody)
       console.log(response);
        }
        catch(e){console.log(e);}
       
      
        
        setUserName('');
        setPassword('');
    };
    const usernameHandler=(e)=>{
        setUserName(e.target.value)
       
    }
    const passwordHandler=(e)=>{
        setPassword(e.target.value)
    }
    return(
    <>
    <div className="image1">
    <Card>
        <form  onSubmit={handleSubmit}> 
            <h1>Lets Tweet today!</h1> 
            <div className='login_top'>
            <div> <h2>Sign in</h2> </div>
            <div className='login_control'>
                <label>Username</label>
                <input
                    type='text'
                    id= 'username'
                    name ='username'
                    onChange={usernameHandler}/>
            </div>
            <div className='login_control'>
                <label>Password</label>
                <input
                    type='text' 
                    id='password'
                    name='password'
                    onChange={passwordHandler}/>
            </div>
            </div>
            <div className='login_actions_submit1'>
                <button type='submit' >Submit</button>
            </div>
            
            <div className='login_actions_register'>
               <a href="">Register</a>
            </div>
            <div className='login_actions_forgotPassword'>
               <a href="">Forgot  password</a>
            </div>
           
            </form>
        </Card>
        </div>
       
         <div><Homepage username={username} /></div>
           
        </>
    )
}

export default Login;
