import React,{useEffect, useState} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import ChatBubbleIcon from '@mui/icons-material/ChatBubble';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import Button from '@material-ui/core/Button';
import {cyan} from "@mui/material/colors";
import './ParticularUsertweets.css';

const ParticularUsertweets=()=>{
    const [usertweets,setUsertweets]=useState([]);
    const{ username }=useParams();
    const getUsertweets= async ()=>{
        const response=await axios.get(`http://localhost:8081/tweets/${username}`)
       console.log(response.data);
       const listtweet=await response.data
       setUsertweets(listtweet);
     }

     useEffect(()=>{
        getUsertweets();
     },[])

     return(
        <> 
        <br></br>          
        {
            usertweets.map((i) => {
               
                return(
                <>
                    
                    <div className="box">
                    <div className="tweetdisplay">
                    <div className="tweetdisplay__body">
                        <div className="tweetdisplay__header">
                            <div className="tweetdisplay_headerText">
                                <h5>
                                    {i.loginId}
                                    <span className="tweetdisplay__createDate">formattedDate({i.creationDate})</span>
                                </h5>
                            </div>
                            <div className="tweetdisplay__headerTitle">
                                    <h4>{i.title}</h4>
                            </div>
                            <div className="tweetdisplay__headerDescription">
                                <p>{i.tweet} </p>
                            </div>
                        </div>
                        <div className="tweetdisplay__footer" >
                           
                            <Button color="primary" variant="contained"><ChatBubbleIcon fontSize="small" style={{color: cyan[50]}}>
                                
                            </ChatBubbleIcon>
                            </Button>
                            <Button color="primary" variant="contained"><ThumbUpIcon fontSize="small" style={{color: cyan[50]}} />{i.likes}</Button>
                            

                        </div>
                    </div>
                </div>
                </div>
               
                </>
                )
            })
        }

  


        </>
    )
}

export default ParticularUsertweets;
